// Define a custom date format function
YAHOO.widget.Column.parseDate = function(sMarkup) {
  if (sMarkup == null || sMarkup.length == 0) {
    return null;
  } else {
    var year = eval(sMarkup.substr(0,4));
    var num = sMarkup.substr(5,2);
    if (num.indexOf("0") == 0) {
      num = num.substr(1,1);
    }
    var month = eval(num) - 1;
    num = sMarkup.substr(8,2);
    if (num.indexOf("0") == 0) {
      num = num.substr(1,1);
    }
    var the_day = eval(num);
    var date = new Date(year, month, the_day);
    return date;
  }
};

YAHOO.widget.Column.months = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];

// Define a custom date format function
YAHOO.widget.Column.formatDate = function(elCell, oRecord, oColumn, oData) {
  if (oData == null) {
    elCell.innerHTML = "";
  } else {
    var oDate = oData;
    var sMonth = YAHOO.widget.Column.months[oDate.getMonth()];
    elCell.innerHTML = oDate.getDate() + " " + sMonth + " " + oDate.getFullYear();
  }
};


